// unminified
jQuery(document).ready(function() {
    function c() {
        fromTop = jQuery(document).scrollTop();
        if (fromTop > 100) {
            jQuery("#header").removeClass("not-scrolled-header").addClass("scrolled-header")
        } else {
            jQuery("#header").removeClass("scrolled-header").addClass("not-scrolled-header")
        }
    }
    c();
    jQuery(document).scroll(function() {
        c()
    });
    documentWidth = jQuery(document).width();
    if (documentWidth > 1024) {
        headerOffset = 60
    } else {
        headerOffset = 60
    }


    function animation(id) {
        jQuery([document.documentElement, document.body]).animate({
            scrollTop: jQuery('#'+id+'-anchor').offset().top - headerOffset
        }, 2000)
    }

    jQuery("#menu-item-15").click(function(e) { // who we are section
    	e.preventDefault();
        animation(jQuery(this).attr('id'));
    });
    jQuery("#menu-item-16").click(function(e) { // what we do section
    	e.preventDefault();
        animation(jQuery(this).attr('id'));
    });
    jQuery("#menu-item-17").click(function(e) { // where we are section
    	e.preventDefault();
        animation(jQuery(this).attr('id'));
    });
    jQuery("#menu-item-18").click(function(e) { // contact-us section
    	e.preventDefault();
        animation(jQuery(this).attr('id'));
    });

    jQuery("#cta-0, #cta-1, #cta-2").click(function(e) { // contact-us section
        e.preventDefault();
        animation('menu-item-18');
    });






    
    
    
    

    function animationInSvg(id) {
        jQuery([document.documentElement, document.body]).animate({
        //jQuery('html,body').animate({
            scrollTop: jQuery(id).offset().top  // + 400
        }, 2000)
    }

    jQuery(document).on('click', 'svg.services a', function(e) {
        e.preventDefault();
        animationInSvg(jQuery(this).attr('xlink:href'));
    });








    // on-top btn
    jQuery('#swich-top').hide();
    jQuery(document).on('scroll', function() {
        var topBtn = jQuery('#swich-top');
        if(jQuery(this).scrollTop() > 600) {
            topBtn.show();
            topBtn.css('cursor', 'pointer');
        } else {
            topBtn.hide();
        }
    });
    jQuery('#swich-top').click(function() {
        jQuery([document.documentElement, document.body]).animate({ scrollTop:0 }, 1000)
    });


    jQuery.fn.moveIt = function() {
        var f = jQuery(window);
        var e = [];
        jQuery(this).each(function() {
            e.push(new a(jQuery(this)))
        });
        window.addEventListener("scroll", function() {
            var g = f.scrollTop();
            e.forEach(function(h) {
                h.update(g)
            })
        }, {
            passive: true
        })
    };
    var a = function(e) {
        this.el = jQuery(e);
        this.speed = parseInt(this.el.attr("data-scroll-speed"))
    };
    a.prototype.update = function(e) {
        this.el.css("transform", "translateY(" + -(e / this.speed) + "px)")
    };
    jQuery(function() {
        jQuery("[data-scroll-speed]").moveIt()
    })
});